long gcd ( int a , int b ) { int i , g ; for ( i = 1 ; i < a ; i ++ ) { if ( a % i == 0 && b % i == 0 ) g = i ; } return g ; } int main ( ) { int t , lc ; long n1 , n2 , gc ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%ld %ld" , & n1 , & n2 ) ; if ( n1 > n2 ) gc = gcd ( n1 , n2 ) ; else gc = gcd ( n2 , n2 ) ; lc = ( n1 * n2 ) / gc ; printf ( "%d %ld\n" , gc , lc ) ; } return 0 ; }