int main ( ) { int store , num1 , num2 , m , check = 0 , tst , hcf ; long long int temp , lcm ; scanf ( "%d" , & tst ) ; while ( check < tst ) { scanf ( "%d%d" , & num1 , & num2 ) ; if ( num1 < num2 ) { temp = num2 ; num2 = num1 ; num1 = temp ; } store = 0 ; for ( m = 1 ; m < 100000 ; m ++ ) { temp = num1 * m ; if ( temp % num2 == 0 ) { lcm = temp ; store = m ; break ; } } hcf = num2 / store ; printf ( "%d %d\n" , lcm , hcf ) ; check ++ ; } }