int main ( ) { int t ; unsigned int n ; long int a [ 50000 ] , sum1 , sum2 , i , j , k ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%u" , & n ) ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%ld" , & a [ j ] ) ; } sum1 = 0 , sum2 = 0 ; while ( n >= 2 ) { if ( a [ 0 ] > a [ 1 ] ) { sum1 = sum1 + a [ 1 ] ; for ( k = 0 ; k < n - 1 ; k ++ ) a [ k ] = a [ k + 1 ] ; n -- ; } else if ( a [ 0 ] < a [ 1 ] ) { sum2 = sum2 + a [ 0 ] ; for ( k = 0 ; k < n - 1 ; k ++ ) a [ k + 1 ] = a [ k + 2 ] ; n -- ; } } printf ( "%ld\n" , sum2 + sum1 ) ; } return 0 ; }