int max_sum ( int a [ 100 ] [ 100 ] , int row_min , int row_max , int col_min ) { int sum = a [ row_min ] [ col_min ] ; if ( row_min == row_max ) return sum ; int sumb , sumr ; sumb = max_sum ( a , row_min + 1 , row_max , col_min ) ; sumr = max_sum ( a , row_min + 1 , row_max , col_min + 1 ) ; if ( sumb > sumr ) sum += sumb ; else sum += sumr ; return sum ; } int main ( ) { int t ; scanf ( "%d" , & t ) ; int i = 0 ; while ( i < t ) { int n ; scanf ( "%d" , & n ) ; int a [ 100 ] [ 100 ] ; int j = 0 ; while ( j < n ) { int k = 0 ; while ( k <= j ) { scanf ( "%d" , & a [ j ] [ k ] ) ; k ++ ; } j ++ ; } int sum = max_sum ( a , 0 , n , 0 ) ; printf ( "%d\n" , sum ) ; i ++ ; } return 0 ; }