int main ( ) { int t ; scanf ( "%d" , & t ) ; int i , k ; long long int n1 , n2 , small , big , lcm , gcd , temp = 1 ; for ( k = 1 ; k <= t ; k ++ ) { temp = 1 ; scanf ( "%lld %lld" , & n1 , & n2 ) ; if ( n1 > n2 ) { small = n2 ; big = n1 ; } else { small = n1 ; big = n2 ; } while ( temp > 0 ) { temp = big % small ; big = small ; small = temp ; } gcd = big ; lcm = ( n1 * n2 ) / gcd ; printf ( "%lld %lld\n" , gcd , lcm ) ; } return 0 ; }