int main ( void ) { long unsigned int T , A , B , i , j , gcd , y , x ; scanf ( "%lu" , & T ) ; for ( i = 1 ; i <= T ; i ++ ) { y = 1 ; printf ( "\n" ) ; scanf ( "%lu %lu" , & A , & B ) ; if ( A < B ) x = A ; else x = B ; for ( j = x ; j >= 1 ; j -- ) { if ( A % j == 0 && B % j == 0 ) { gcd = j ; break ; } } for ( i = x ; i >= 2 ; i -- ) { if ( A % i == 0 && B % i == 0 ) { y = y * i ; A = A / i ; B = B / i ; } } printf ( "\n%lu %lu" , gcd , y * A * B ) ; } return 0 ; }