int gcd ( int , int ) ; int lcm ( int , int ) ; int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t != 0 ) { int x ; scanf ( "%d" , & x ) ; int y ; scanf ( "%d" , & y ) ; int c = gcd ( x , y ) ; printf ( "%d\t" , c ) ; int d = lcm ( x , y ) ; printf ( "%d\n" , d ) ; t -- ; } return 0 ; } int gcd ( int a , int b ) { while ( a % b != 0 ) { int p = a % b ; a = b ; b = p ; } return b ; } int lcm ( int a , int b ) { int min ; if ( a < b ) min = b ; min = a ; int i ; for ( i = min ; i <= ( a * b ) ; i = i + min ) { if ( i % a == 0 && i % b == 0 ) break ; } return i ; }