long int fastInput ( ) { long int n = 0 , c = gc ( ) ; while ( c < '0' || c > '9' ) c = gc ( ) ; while ( c >= '0' && c <= '9' ) n = ( n << 3 ) + ( n << 1 ) + c - '0' , c = gc ( ) ; return n ; } int main ( void ) { long int i , T , N , min , temp , ans ; T = fastInput ( ) ; for ( i = 0 ; i < T ; i ++ ) { N = fastInput ( ) ; min = fastInput ( ) ; for ( i = 1 ; i < N ; i ++ ) { temp = fastInput ( ) ; if ( temp < min ) min = temp ; } ans = min * ( N - 1 ) ; printf ( "%lld\n" , ans ) ; } return 0 ; }