int main ( ) { int N , T ; long int A , B ; long int count , tempC ; int flag ; scanf ( "%d" , & T ) ; while ( T -- ) { scanf ( "%d" , & N ) ; count = N ; tempC = 1 ; flag = 0 ; if ( N > 1 ) { scanf ( "%ld %ld" , & A , & B ) ; N -- ; N -- ; if ( B > A ) { tempC ++ ; } while ( N -- ) { A = B ; scanf ( "%ld" , & B ) ; if ( B > A ) { tempC ++ ; } else { count = count + ( tempC - 1 ) ; count = count + ( tempC * ( tempC - 1 ) / 2 ) ; tempC = 1 ; } } } else { scanf ( "%ld" , & A ) ; } printf ( "%ld\n" , count ) ; } return 0 ; }