int gcd ( int a , int b ) { int temp ; while ( b != 0 ) { temp = a % b ; a = b ; b = temp ; } return a ; } main ( ) { int t ; scanf ( "%d" , & t ) ; int i , arr [ t ] [ 2 ] ; for ( i = 0 ; i < t ; i ++ ) { int a , b ; scanf ( "%d %d" , & a , & b ) ; if ( a >= 1 && b >= 1 && a <= 1000000 && b <= 1000000 ) { arr [ i ] [ 0 ] = gcd ( a , b ) ; arr [ i ] [ 1 ] = ( a * b ) / ( arr [ i ] [ 0 ] ) ; } } for ( i = 0 ; i < t ; i ++ ) printf ( "%d %d\n" , arr [ i ] [ 0 ] , arr [ i ] [ 1 ] ) ; }