int main ( ) { int n1 , n2 , x , a , y , gcd , i ; int w [ a ] ; int v [ a ] ; scanf ( "%d" , & a ) ; for ( i = 0 ; i < a ; i ++ ) { scanf ( "%d %d" , & n1 , & n2 ) ; x = n1 ; y = n2 ; while ( n1 != n2 ) { if ( n1 > n2 ) { n1 = n1 - n2 ; } else { n2 = n2 - n1 ; } } gcd = n1 ; int lcm = ( x * y ) / gcd ; w [ i ] = n1 ; v [ i ] = lcm ; } for ( i = 0 ; i < a ; i ++ ) { printf ( "\n%d" , w [ i ] ) ; printf ( " %d" , v [ i ] ) ; } return 0 ; }