int main ( void ) { int t ; scanf ( "%d" , & t ) ; for ( int i = 1 ; i <= t ; i ++ ) { int n ; scanf ( "%d" , & n ) ; int a [ n ] , sum = 0 ; for ( int i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } for ( int j = 0 ; j < n ; j ++ ) { if ( a [ j ] != '\0' ) { if ( a [ j ] > a [ j + 1 ] ) { a [ j ] = '\0' ; sum += a [ j + 1 ] ; } else { a [ j + 1 ] = '\0' ; sum += a [ j ] ; } } } printf ( "%d\n" , sum ) ; } return 0 ; }