int main ( ) { int T , h , l ; scanf ( "%d" , & T ) ; while ( T -- ) { int p , q ; scanf ( "%d%d" , & p , & q ) ; h = hcf ( p , q ) ; l = lcm ( p , q ) ; printf ( "%d\t%d" , h , l ) ; } return 0 ; } int hcf ( int a , int b ) { while ( a != b ) { if ( a > b ) { a = a - b ; } else { b = b - a ; } } return a ; } int lcm ( int i , int k ) { static int multiple = 0 ; multiple += k ; if ( ( multiple % i == 0 ) && ( multiple % k == 0 ) ) { return multiple ; } else { return lcm ( i , k ) ; } }