int main ( ) { int max , lines , testcases , i , j , k , arr1 [ 101 ] , arr2 [ 100 ] ; scanf ( "%d" , & testcases ) ; for ( i = 0 ; i < testcases ; i ++ ) { scanf ( "%d" , & lines ) ; arr1 [ 0 ] = 0 ; arr1 [ 1 ] = 0 ; for ( j = 1 ; j <= lines ; j ++ ) { for ( k = 0 ; k < j - 1 ; k ++ ) { scanf ( "%d " , & arr2 [ k ] ) ; } scanf ( "%d" , & arr2 [ j - 1 ] ) ; for ( k = 0 ; k < j ; k ++ ) { arr1 [ j - k ] = arr2 [ j - 1 - k ] + ( arr1 [ j - k ] > arr1 [ j - 1 - k ] ? arr1 [ j - k ] : arr1 [ j - 1 - k ] ) ; } arr1 [ 0 ] = 0 ; arr1 [ j + 1 ] = 0 ; } max = 0 ; for ( j = 0 ; j < lines + 1 ; j ++ ) max = arr1 [ j ] > max ? arr1 [ j ] : max ; printf ( "%d\n" , max ) ; } return 0 ; }