int gcd ( int , int ) ; int lcm ( int , int ) ; int main ( ) { int t ; int a , b ; int one , two ; scanf ( "%d" , & t ) ; for ( ; t > 0 ; t -- ) { do { scanf ( "%d%d" , & a , & b ) ; } while ( a < 1 || a > 1000000 || b < 1 || b > 1000000 ) ; one = gcd ( a , b ) ; two = lcm ( a , b ) ; printf ( "%d %d\n" , one , two ) ; } return 0 ; } int gcd ( int x , int y ) { int i ; int l ; int p = 1 ; if ( x <= y ) l = x ; else l = y ; for ( i = 1 ; i <= l ; i ++ ) { if ( x % i == 0 && y % i == 0 ) { p = i ; } } return p ; } int lcm ( int x , int y ) { int i ; int h ; int p ; if ( x <= y ) h = y ; else h = x ; for ( i = x * y ; i >= h ; i -- ) { if ( i % x == 0 && i % y == 0 ) p = i ; } return p ; }