int main ( ) { int t , n1 , n2 , i , gcd , lcm ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d" , & n1 , & n2 ) ; if ( n1 > n2 ) { for ( i = 1 ; i <= n2 ; i ++ ) { if ( n1 % i == 0 && n2 % i == 0 ) gcd = i ; } } else { for ( i = 1 ; i <= n1 ; i ++ ) { if ( n1 % i == 0 && n2 % i == 0 ) gcd = i ; } } lcm = ( n1 * n2 ) / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }