int find_gcd ( int x , int y ) { if ( y == 0 ) return x ; else return find_gcd ( y , x % y ) ; } int main ( ) { int T , a , b , ans = 0 , * arr , gcd , lcm ; scanf ( "%d" , & T ) ; arr = ( int * ) malloc ( T * 2 * sizeof ( int ) ) ; while ( T -- ) { scanf ( "%d%d" , & a , & b ) ; gcd = find_gcd ( a , b ) ; lcm = ( a * b ) / gcd ; arr [ ans ++ ] = gcd ; arr [ ans ++ ] = lcm ; } for ( T = 0 ; T < ans ; T += 2 ) { printf ( "%d %d" , arr [ T ] , arr [ T + 1 ] ) ; if ( T != ans - 2 ) printf ( "\n" ) ; } }