int main ( ) { int n , t ; scanf ( "%d" , & n ) ; while ( n -- ) { scanf ( "%d" , & t ) ; int a [ t ] [ t ] ; for ( int i = 0 ; i < t ; i ++ ) { for ( int j = 0 ; j <= i ; j ++ ) scanf ( "%d" , & a [ i ] [ j ] ) ; } for ( int i = t - 1 ; i > 0 ; i -- ) for ( int j = 0 ; j < i ; j ++ ) { if ( a [ i ] [ j ] > a [ i ] [ j + 1 ] ) a [ i - 1 ] [ j ] += a [ i ] [ j ] ; else a [ i - 1 ] [ j ] += a [ i ] [ j + 1 ] ; } printf ( "%d\n" , a [ 0 ] [ 0 ] ) ; } return 0 ; }