int main ( void ) { int t , a , b ; long long int c , d ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; d = a * b ; if ( a > b ) { while ( 1 ) { c = b ; if ( a % b == 0 ) { printf ( "%d %lld\n" , b , d / b ) ; break ; } b = a % b ; a = c ; } } else if ( a < b ) { while ( 1 ) { c = a ; if ( b % a == 0 ) { printf ( "%d %lld\n" , a , d / a ) ; break ; } a = b % a ; b = c ; } } else if ( a == b ) printf ( "%d %d\n" , a , b ) ; } }