int main ( ) { int t , r , i , sum = 0 , l , m , s = 0 , max ; scanf ( "%d" , & t ) ; while ( -- t ) { scanf ( "%d" , & r ) ; s = r * ( r + 1 ) / 2 ; int ar [ s ] ; max = ar [ 0 ] + ar [ 1 ] + ar [ 2 ] + ar [ 3 ] ; l = 0 ; while ( l <= ( s - 4 ) ) { for ( i = l ; i < l + 4 ; i ++ ) { sum = sum + ar [ i ] ; } if ( sum > max ) max = sum ; l ++ ; } printf ( "%d\n" , max ) ; } return 0 ; }