int main ( ) { long int t , n1 , n2 , rem , num , den , gcd , lcm ; scanf ( "%ld" , & t ) ; while ( t -- ) { scanf ( "%ld %ld" , & n1 , & n2 ) ; if ( n1 > n2 ) { num = n1 ; den = n2 ; } else { num = n2 ; den = n1 ; } rem = num % den ; while ( rem != 0 ) { num = den ; den = rem ; rem = num % den ; } gcd = den ; lcm = ( n1 * n2 ) / gcd ; printf ( "%ld" , gcd ) ; printf ( " %ld\n" , lcm ) ; } }