int gcd ( int a , int b ) { if ( b == 0 ) return a ; else return gcd ( b , a % b ) ; } int lcm ( int a , int b ) { int temp = a ; while ( 1 ) { if ( temp % b == 0 && temp % a == 0 ) break ; temp ++ ; } return temp ; } int main ( ) { int l , t , a , b , g ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d" , & a , & b ) ; g = gcd ( a , b ) ; l = lcm ( a , b ) ; printf ( "%d %d" , g , l ) ; } return 0 ; }