int main ( ) { int t ; scanf ( "%d" , & t ) ; for ( int i = 0 ; i < t ; i ++ ) { int a , b ; int gcd , lcm ; scanf ( "%d%d" , & a , & b ) ; int A = a , B = b ; if ( a > b ) { int r = a % b ; while ( r != 0 ) { a = b ; b = r ; r = a % b ; } gcd = b ; } else { int r = b % a ; while ( r != 0 ) { b = a ; a = r ; r = b % a ; } gcd = a ; } lcm = A * B / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }