long long int gcd ( long long int a , int b ) { if ( a == 0 || b == 0 ) { return 0 ; } if ( a == b ) { return a ; } if ( a > b ) { return gcd ( a - b , b ) ; } return gcd ( a , b - a ) ; } int main ( void ) { long long int t , a , b , g , l ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%lld %lld" , & a , & b ) ; g = gcd ( a , b ) ; l = ( a * b ) / g ; printf ( "%lld %lld\n" , g , l ) ; } return 0 ; }