int main ( ) { int T , i ; scanf ( "%d" , & T ) ; while ( T -- ) { int A , B , GCD , LCM ; scanf ( "%d%d" , & A , & B ) ; if ( A > B ) { for ( i = 1 ; i <= A ; i ++ ) { if ( A % i == 0 && B % i == 0 ) GCD = i ; } LCM = ( A * B ) / GCD ; printf ( "%d %d" , GCD , LCM ) ; } else if ( B > A ) { for ( i = 1 ; i <= B ; i ++ ) { if ( A % i == 0 && B % i == 0 ) GCD = i ; } LCM = ( A * B ) / GCD ; printf ( "%d %d" , GCD , LCM ) ; } } return 0 ; }