int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int i , n ; scanf ( "%d" , & n ) ; int arr [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & arr [ i ] ) ; int min = arr [ 0 ] ; for ( i = 1 ; i < n ; i ++ ) { if ( arr [ i ] < min ) min = arr [ i ] ; } printf ( "%d\n" , min * ( n - 1 ) ) ; } return 0 ; }