int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b , i = 1 , gcd = 0 , lcm = 1 , min = 0 , max = 0 ; scanf ( "%d%d" , & a , & b ) ; if ( a > b ) { min = b ; max = a ; } else { min = a ; max = b ; } while ( i != 0 ) { i = max % min ; if ( i == 0 ) break ; else { max = min ; min = i ; } } gcd = min ; lcm = ( a * b ) / gcd ; printf ( "\n%d  %d" , gcd , lcm ) ; } return 0 ; }