int main ( ) { long int num1 , num2 , gcd , lcm , a , i , testcases ; x : scanf ( "%d" , & testcases ) ; if ( testcases <= 1000 && testcases >= 1 ) { for ( i = 1 ; i <= testcases ; i ++ ) { y : scanf ( "%d %d" , & num1 , & num2 ) ; if ( num1 <= 1000000 && num2 <= 1000000 && num1 >= 1 && num2 >= 1 ) { if ( num1 > num2 ) { a = num1 ; num1 = num2 ; num2 = a ; } for ( i = num1 ; i > 1 ; i -- ) { if ( num1 % i == 0 ) { if ( num2 % i == 0 ) { gcd = i ; break ; } } } lcm = num1 * num2 / gcd ; printf ( "LCM= %ld GCD= %ld\n" , lcm , gcd ) ; } else goto y ; } } else goto x ; return 0 ; }