int main ( ) { int T , i ; scanf ( "%d" , & T ) ; unsigned long int out [ T ] ; for ( i = 0 ; i < T ; i ++ ) { int N , j , smlst ; scanf ( "%d" , & N ) ; int ar [ N ] ; for ( j = 0 ; j < N ; j ++ ) scanf ( "%d" , & ar [ j ] ) ; smlst = ar [ 0 ] ; for ( j = 0 ; j < N ; j ++ ) { if ( ar [ j ] < smlst ) smlst = ar [ j ] ; } out [ i ] = smlst * ( N - 1 ) ; } for ( i = 0 ; i < T ; i ++ ) printf ( "%lu\n" , out [ i ] ) ; return 0 ; }