int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int n , i , min = 0 ; int sum = 0 ; scanf ( "%lld" , & n ) ; long long int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & a [ i ] ) ; } min = a [ 0 ] ; for ( i = 0 ; i < n ; i ++ ) { if ( a [ i ] < min ) min = a [ i ] ; } printf ( "%d" , sum += ( min * ( n - 1 ) ) ) ; } return 0 ; }