int gcd ( int n1 , int n2 ) { if ( n2 != 0 ) return gcd ( n2 , n1 % n2 ) ; else return n1 ; } int main ( ) { int n , i , j , A , B , GCD ; long int LCM ; scanf ( "%d\n" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d %d\n" , & A , & B ) ; GCD = gcd ( A , B ) ; for ( j = 1 ; j <= A && j <= B ; ++ j ) { if ( A % j == 0 && B % j == 0 ) GCD = j ; } LCM = ( A * B ) / GCD ; printf ( "%d %d\n" , gcd ( A , B ) , LCM ) ; } return 0 ; }