int getGcd ( int a , int b ) { if ( a == 0 ) return b ; else if ( b == 0 ) return a ; else return getGcd ( b , a % b ) ; } int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b ; scanf ( "%d%d" , & a , & b ) ; int gcd = getGcd ( a , b ) ; printf ( "%d %d\n" , gcd , a * b / gcd ) ; } return 0 ; }