int main ( ) { int t ; scanf ( "%d" , & t ) ; int i , j ; for ( j = 1 ; j <= t ; j ++ ) { int n ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } int min = a [ 0 ] ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ i ] < min ) min = a [ i ] ; } long long int cost = ( long long int ) ( ( n - 1 ) * min ) ; printf ( "%lld\n" , cost ) ; } return 0 ; }