int main ( void ) { int n , t , a , c , j ; int s [ SIZE ] , k , sum = 0 ; scanf ( "%d" , & t ) ; for ( a = 1 ; a <= t ; a ++ ) { sum = 0 ; k = 0 ; scanf ( "%d" , & n ) ; for ( int i = 0 ; i < n ; i ++ ) scanf ( "%d" , & s [ i ] ) ; for ( int i = 0 ; i < n - 1 ; i ++ ) { c = i ; for ( j = 0 ; j < n ; j ++ ) { if ( s [ j ] < s [ i ] ) c = j ; } } k = s [ c ] ; for ( int i = 0 ; i < n - 1 ; i ++ ) sum += k ; printf ( "%d\n" , sum ) ; } return 0 ; }