int main ( void ) { int n , i ; scanf ( "%d" , & n ) ; unsigned long long int a , b , gcd , lcm , rem , num , den ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld %lld" , & a , & b ) ; if ( a > b ) { num = a ; den = b ; } else { num = b ; den = a ; } rem = num % den ; while ( rem != 0 ) { num = den ; den = rem ; rem = num % den ; } gcd = den ; lcm = a * ( b / gcd ) ; printf ( "%lld %lld\n" , gcd , lcm ) ; } return 0 ; }