int main ( ) { int t ; int n ; int i ; long long int min ; long long int * arr ; scanf ( "%d\n" , & t ) ; while ( t -- ) { scanf ( "%d\n" , & n ) ; arr = ( long long int * ) calloc ( n , sizeof ( long long int ) ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%lld" , & arr [ i ] ) ; min = arr [ 0 ] ; for ( i = 1 ; i < n ; i ++ ) if ( min > arr [ i ] ) min = arr [ i ] ; printf ( "%lld\n" , min * ( n - 1 ) ) ; } return 0 ; }