void main ( ) { int p ; int remainder , numerator , denominator ; long int num1 , num2 ; scanf ( "%d" , & p ) ; long int gcd [ p ] , lcm [ p ] ; int i ; for ( i = 0 ; i < p ; i ++ ) { scanf ( "%ld %ld" , & num1 , & num2 ) ; if ( num1 > num2 ) { numerator = num1 ; denominator = num2 ; } else { numerator = num2 ; denominator = num1 ; } remainder = numerator % denominator ; while ( remainder != 0 ) { numerator = denominator ; denominator = remainder ; remainder = numerator % denominator ; } gcd [ i ] = denominator ; lcm [ i ] = num1 * num2 / gcd [ i ] ; } for ( i = 0 ; i < p ; i ++ ) { printf ( "%ld %ld\n" , gcd [ i ] , lcm [ i ] ) ; } }