int main ( ) { int test ; scanf ( "%d" , & test ) ; if ( test < 1 || test > 1000 ) { return 0 ; } else { int ans [ 2 * test ] ; int ui ; int t = 0 ; for ( ui = 0 ; ui < test ; ui ++ ) { int a ; int b ; scanf ( "%d %d" , & a , & b ) ; if ( ( a < 1 || a > 1000000 ) || ( b < 1 || b > 1000000 ) ) { return 0 ; } else { int lcm = 1 , hcf = 1 ; int temp ; if ( a > b ) { temp = a ; a = b ; b = temp ; } int x = a , y = b ; int i ; int lcmdone = 0 ; int count = 0 ; for ( i = 2 ; i <= b ; i ++ ) { if ( x % i == 0 ) { lcm *= i ; x /= i ; lcmdone ++ ; count ++ ; } if ( y % i == 0 ) { if ( lcmdone == 0 ) { lcm *= i ; } y /= i ; lcmdone = 0 ; count ++ ; } if ( count != 0 ) { i = 1 ; count = 0 ; lcmdone = 0 ; } } hcf = a * b / lcm ; ans [ t ++ ] = hcf ; ans [ t ++ ] = lcm ; } } int q ; for ( q = 0 ; q < 2 * test ; q = q + 2 ) { printf ( "%d %d" , ans [ q ] , ans [ q + 1 ] ) ; printf ( "\n" ) ; } } }