int LCM ( int a , int b ) { if ( a % b == 0 ) return a ; else if ( b % a == 0 ) return b ; else return a * b ; } int GCD ( int a , int b ) { while ( b > 0 ) { int temp ; temp = b ; b = a % b ; a = temp ; } return a ; } int main ( ) { int T , A , B , i ; scanf ( "%d" , & T ) ; for ( i = 0 ; i < T ; ++ i ) { scanf ( "%d %d" , & A , & B ) ; printf ( "%d %d" , GCD ( A , B ) , LCM ( A , B ) ) ; } return 0 ; }