long long int gcd_fn ( int a , int b ) { if ( b > a ) { b = a + b ; a = b - a ; b = b - a ; } long long int div1 = a ; long long int ds = b ; long long int rem , ans ; while ( ds > 0 ) { rem = div1 % ds ; ans = ds ; div1 = ds ; ds = rem ; } return ans ; } int main ( void ) { long long int t , a , b , gcd , lcm , i ; scanf ( "%lld" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lld%lld" , & a , & b ) ; gcd = gcd_fn ( a , b ) ; lcm = ( a * b ) / gcd ; printf ( "%lld %lld\n" , gcd , lcm ) ; } return 0 ; }