int main ( ) { int t , u ; scanf ( "%d" , & t ) ; for ( u = 1 ; u <= t ; u ++ ) { int n , i , j ; scanf ( "%d" , & n ) ; int arr [ n ] [ n ] ; int sum = 0 ; int m = 0 ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & arr [ i ] [ j ] ) ; } for ( j = 0 ; j <= i ; j ++ ) { if ( arr [ i ] [ j ] > m ) ; m = arr [ i ] [ j ] ; } sum = sum + m ; m = 0 ; } printf ( "%d\n" , sum ) ; } return 0 ; }