int gcd ( int f , int a , int b ) ; int lcm ( int g , int a , int b ) ; main ( ) { int t , a , b , i , f , g ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; f = ( a < b ? a : b ) ; g = ( a > b ? a : b ) ; printf ( "%d %d\n" , gcd ( f , a , b ) , lcm ( g , a , b ) ) ; } } int gcd ( int f , int a , int b ) { if ( a % f == 0 && b % f == 0 ) return f ; else return gcd ( f - 1 , a , b ) ; } int lcm ( int g , int a , int b ) { int i ; for ( i = g ; i <= a * b ; i ++ ) { if ( i % a == 0 && i % b == 0 ) return i ; } }