int main ( ) { int n , i , j , k , e , a [ 100 ] [ 100 ] ; scanf ( "%d" , & n ) ; int sum [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & e ) ; int c = 0 ; for ( j = 0 ; j < e ; j ++ ) { for ( k = 0 ; k < j + 1 ; k ++ ) { scanf ( "%d" , & a [ j ] [ k ] ) ; c = c + a [ j ] [ k ] ; } } sum [ i ] = c ; } for ( i = 0 ; i < n ; i ++ ) { printf ( "%d\n" , sum [ i ] / 2 ) ; } return 0 ; }