int arr [ 100 ] [ 100 ] , b , maxi = 0 ; int main ( ) { int i , j , k , a ; scanf ( "%d\n" , & a ) ; for ( i = 0 ; i < a ; i ++ ) { scanf ( "%d\n" , & b ) ; for ( j = 0 ; j < b ; j ++ ) { for ( k = 0 ; k <= j ; k ++ ) scanf ( "%d" , & arr [ j ] [ k ] ) ; } recur ( 0 , 0 , 0 ) ; printf ( "%d\n" , maxi ) ; } return 0 ; } int recur ( int k , int i , int sum ) { if ( k < b ) { sum += arr [ k ] [ i ] ; recur ( k + 1 , i , sum ) ; recur ( k + 1 , i + 1 , sum ) ; } if ( sum > maxi ) maxi = sum ; }