int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b , max ; long long int gcd , lcm ; scanf ( "%d%d" , & a , & b ) ; if ( a > b ) { max = a ; } else { max = b ; } for ( int i = max ; i >= 1 ; i -- ) { if ( a % i == 0 && b % i == 0 ) { gcd = i ; break ; } } for ( int i = max ; ; i ++ ) { if ( i % a == 0 && i % b == 0 ) { lcm = i ; break ; } } printf ( "%lld %lld " , gcd , lcm ) ; } return 0 ; }