int main ( ) { int t , i , j , k ; unsigned long int arr [ 50000 ] ; unsigned int n ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lu" , & n ) ; if ( t >= 1 && t <= 10 && n >= 2 && n <= 50000 ) { for ( j = 0 ; j < n ; j ++ ) { scanf ( "%llu" , & arr [ j ] ) ; } int sum = 0 ; for ( k = 0 ; k < n - 1 ; k ++ ) { if ( arr [ k ] < arr [ k + 1 ] ) { sum = sum + arr [ k ] ; arr [ k + 1 ] = arr [ k ] ; } else { sum = sum + arr [ k + 1 ] ; } } printf ( "%d\n" , sum ) ; } } return 0 ; }