int main ( ) { int t , n , a , j , i ; int sum [ 1000 ] , sum1 , sum2 ; int max [ 100 ] , size = 0 ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { sum [ i ] = 0 ; for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & a ) ; if ( j == 0 ) { sum2 = sum [ j ] ; sum [ j ] = sum [ j ] + a ; } else { sum1 = sum [ j ] ; if ( ( sum2 + a ) > ( sum [ j ] + a ) ) sum [ j ] = sum2 + a ; else sum [ j ] = sum [ j ] + a ; sum2 = sum1 ; } } } max [ size ] = sum [ 0 ] ; for ( i = 0 ; i < n ; i ++ ) if ( max [ size ] < sum [ i ] ) max [ size ] = sum [ i ] ; size ++ ; } for ( i = 0 ; i < size ; i ++ ) printf ( "\n%d" , max [ i ] ) ; return 0 ; }