void cmmd ( int a , int b ) { if ( b == 0 ) printf ( "%d " , a ) ; else cmmd ( b , a % b ) ; } void func ( int a , int b ) { int max ; if ( a > b ) max = a ; else max = b ; while ( 1 ) { if ( max % a == 0 && max % b == 0 ) { break ; } max ++ ; } printf ( "%d\n" , max ) ; } int main ( ) { int a , b , T ; scanf ( "%d" , & T ) ; while ( T > 0 ) { scanf ( "%d%d" , & a , & b ) ; cmmd ( a , b ) ; func ( a , b ) ; } return 0 ; }