int main ( ) { int t , a , b , ans , gcd , i , lcm , n1 , n2 ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; ans = ( a < b ) ? a : b ; for ( i = ans ; i >= 1 ; i -- ) { if ( a % i == 0 && b % i == 0 ) { printf ( "%d " , i ) ; break ; } } n1 = a ; n2 = b ; while ( n1 != n2 ) { if ( n1 > n2 ) { n1 = n1 - n2 ; } else n2 = n2 - n1 ; } lcm = a * b / n1 ; printf ( "%d\n" , lcm ) ; } return 0 ; }