int main ( ) { int n , a , b , num , den , gcd , lcm , rem ; scanf ( "%d" , & n ) ; while ( n -- ) { scanf ( "%d  %d" , & a , & b ) ; if ( a > b ) { num = a ; den = b ; } else { num = b ; den = a ; } rem = num % den ; while ( rem != 0 ) { num = den ; den = rem ; rem = num % den ; } gcd = den ; lcm = ( a * b ) / gcd ; printf ( "%d  %d\n" , gcd , lcm ) ; gcd = 0 ; lcm = 0 ; } return 0 ; }